import React from 'react';  
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom'; // Updated from Switch to Routes  
import Register from './Register';  
import MessagesList from './SendMessages';  // Updated import to match the renamed filename  

function App() {  
  return (  
    <Router>  
      <div className="container">  
        <header>  
          <h1>Parent-Teacher Communication App</h1>  
          <nav className="navbar navbar-expand-lg navbar-light bg-light">  
            <div className="collapse navbar-collapse" id="navbarSupportedContent">  
              <ul className="navbar-nav me-auto mb-2 mb-lg-0">  
                <li className="nav-item">  
                  <Link className="nav-link" to="/">Home</Link>  
                </li>  
                <li className="nav-item">  
                  <Link className="nav-link" to="/messages">View Messages</Link>  {/* Link to MessagesList */}  
                </li>  
                <li className="nav-item">  
                  <Link className="nav-link" to="/register">Register</Link>  
                </li>  
              </ul>  
            </div>  
          </nav>  
        </header>  

        <Routes>  {/* Updated from Switch to Routes */}  
          <Route path="/" element={<Home />} />  {/* Updated from component to element */}  
          <Route path="/messages" element={<MessagesList />} />  {/* Updated to point to SendMessages */}  
          <Route path="/register" element={<Register />} />  
        </Routes>  
      </div>  
    </Router>  
  );  
}  

function Home() {  
  return (  
    <div>  
      <h2>Welcome to the Parent-Teacher Communication App!</h2>  
      <p>This platform facilitates effective communication between parents and teachers.</p>  
    </div>  
  );  
}  

export default App;
