import React from 'react';  
import { Route, Link, Routes } from 'react-router-dom';  
import Register from './Register';  
import MessagesList from './SendMessages'; // Updated import to match the renamed filename  

const App = () => {  
  return (  
    <div className="container">  
      <header>  
        <h1>Parent-Teacher Communication App</h1>  
        <nav className="navbar navbar-expand-lg navbar-light bg-light">  
          <div className="collapse navbar-collapse" id="navbarSupportedContent">  
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">  
              <li className="nav-item">  
                <Link className="nav-link" to="/">Home</Link>  
              </li>  
              <li className="nav-item">  
                <Link className="nav-link" to="/messages">View Messages</Link>  
              </li>  
              <li className="nav-item">  
                <Link className="nav-link" to="/register">Register</Link>  
              </li>  
              <li className="nav-item">  
                <Link className="nav-link" to="/announcements">Announcements</Link> {/* Added link for announcements */}  
              </li>  
            </ul>  
          </div>  
        </nav>  
      </header>  

      <Routes>   
        <Route path="/" element={<Home />} />  
        <Route path="/messages" element={<MessagesList />} />  
        <Route path="/register" element={<Register />} />  
        <Route path="/announcements" element={<Announcements />} /> {/* Added route for Announcements */}  
      </Routes>  
    </div>  
  );  
}  

const Home = () => {  
  return (  
    <div>  
      <h2>Welcome to the Parent-Teacher Communication App!</h2>  
      <p>This platform facilitates effective communication between parents and teachers.</p>  
    </div>  
  );  
};  

// New Announcements component  
const Announcements = () => {  
  return (  
    <div>  
      <h2>Announcements</h2>  
      <p>Stay tuned for the latest updates and events in our school community.</p>  
    </div>  
  );  
};  

export default App;
