import React, { useEffect } from 'react';  

function Messages({ messages, fetchMessages, handleSubmit, content, setContent }) {  
  return (  
    <div>  
      <h2>Messages</h2>  
      <ul className="list-group">  
        {messages.length === 0 ? (  
          <li className="list-group-item">No messages found.</li>  
        ) : (  
          messages.map((message) => (  
            <li key={message.id} className="list-group-item">{message.content}</li>  
          ))  
        )}  
      </ul>  

      <h2>Send a Message</h2>  
      <form onSubmit={handleSubmit}>  
        <div className="mb-3">  
          <label htmlFor="content" className="form-label">Message:</label>  
          <input  
            type="text"  
            id="content"  
            name="content"  
            value={content}  
            onChange={(e) => setContent(e.target.value)}  
            className="form-control"  
            required  
          />  
        </div>  
        <button type="submit" className="btn btn-primary">Send</button>  
      </form>  
    </div>  
  );  
}  

export default Messages;
