import React, { useState, useEffect } from 'react';  
import { Dropdown } from 'react-bootstrap';  
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS  
import Register from './Register'; // Import the Register component  

function App() {  
  const [messages, setMessages] = useState([]);  
  const [content, setContent] = useState('');  
  const [feedback, setFeedback] = useState('');  
  const apiUrl = 'http://127.0.0.1:8000/api/messages/'; // Your API endpoint  
  const [showRegister, setShowRegister] = useState(false); // State to toggle registration form 

  // Replace this with your actual token  
  const token = 'your_token_here';   

  const fetchMessages = async () => {  
    try {  
      const response = await fetch(apiUrl, {  
        method: 'GET',  
        headers: {  
          'Authorization': `Token ${token}`, // Ensuring the token is included  
          'Content-Type': 'application/json',  
        },  
      });  
      if (!response.ok) {  
        throw new Error('Failed to fetch messages');  
      }  
      const data = await response.json();  
      setMessages(data);  
    } catch (error) {  
      console.error('Error fetching messages:', error);  
      setFeedback('Error fetching messages.');  
    }  
  };  

  const handleSubmit = async (e) => {  
    e.preventDefault();  
    if (!content) return;  

    try {  
      const response = await fetch(apiUrl, {  
        method: 'POST',  
        headers: {  
          'Authorization': `Token ${token}`,  
          'Content-Type': 'application/json',  
        },  
        body: JSON.stringify({ content }),  
      });  

      if (!response.ok) {  
        throw new Error('Failed to send message');  
      }  

      setFeedback('Message sent!');  
      setContent('');  
      fetchMessages();  
    } catch (error) {  
      console.error('Error sending message:', error);  
      setFeedback('Error sending message.');  
    }  
  };  

  useEffect(() => {  
    fetchMessages();  
  }, []);  

  return (  
    <div className="container">  
      <h1>Parent-Teacher Communication App</h1>  
      {/* Dropdown Navigation */}  
      <Dropdown>  
        <Dropdown.Toggle variant="success" id="dropdown-basic">  
          Navigate  
        </Dropdown.Toggle>  
        <Dropdown.Menu>  
          <Dropdown.Item href="#/messages">View Messages</Dropdown.Item>  
          <Dropdown.Item href="#/send-message">Send Message</Dropdown.Item>  
          {/* Add more items as needed */}  
        </Dropdown.Menu>  
      </Dropdown>  

      {feedback && <p className="alert alert-info">{feedback}</p>}  
      <div>  
        <h2>Messages</h2>  
        <ul className="list-group">  
          {messages.map((message) => (  
            <li key={message.id} className="list-group-item">{message.content}</li>  
          ))}  
        </ul>  
      </div>  
      <div>  
        <h2>Send a Message</h2>  
        <form onSubmit={handleSubmit}>  
          <div className="mb-3">  
            <label htmlFor="content" className="form-label">Message:</label>  
            <input  
              type="text"  
              id="content"  
              name="content"  
              value={content}  
              onChange={(e) => setContent(e.target.value)}  
              className="form-control"  
              required  
            />  
          </div>  
          <button type="submit" className="btn btn-primary">Send</button>  
        </form>  
      </div>  
    </div>  
  );  
}  

export default App;
