import React from 'react';  
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';  
import Register from './Register';  
import MessagesList from './Messages';  // Renamed import to avoid conflict  
import SendMessage from './SendMessage';  // Importing this correctly  

function App() {  
  return (  
    <Router>  
      <div className="container">  
        <header>  
          <h1>Parent-Teacher Communication App</h1>  
          <nav className="navbar navbar-expand-lg navbar-light bg-light">  
            <div className="collapse navbar-collapse" id="navbarSupportedContent">  
              <ul className="navbar-nav me-auto mb-2 mb-lg-0">  
                <li className="nav-item">  
                  <Link className="nav-link" to="/">Home</Link>  
                </li>  
                <li className="nav-item">  
                  <Link className="nav-link" to="/messages">View Messages</Link>  
                </li>  
                <li className="nav-item">  
                  <Link className="nav-link" to="/send-message">Send Message</Link>  
                </li>  
                <li className="nav-item">  
                  <Link className="nav-link" to="/register">Register</Link>  
                </li>  
              </ul>  
            </div>  
          </nav>  
        </header>  

        <Switch>  
          <Route path="/" exact component={Home} />  
          <Route path="/messages" component={MessagesList} />  {/* Updated to use renamed Messages component */}  
          <Route path="/send-message" component={SendMessage} />  {/* Ensure this matches the import */}  
          <Route path="/register" component={Register} />  
        </Switch>  
      </div>  
    </Router>  
  );  
}  

function Home() {  
  return (  
    <div>  
      <h2>Welcome to the Parent-Teacher Communication App!</h2>  
      <p>This platform facilitates effective communication between parents and teachers.</p>  
    </div>  
  );  
}  

// Ensure no duplicate SendMessage function exists here  
// If you have commented out code in your previous implementation, make sure it is either fully removed or correctly commented.  

function SendMessageComponent() {  // Renamed local declaration to avoid conflict  
  return (  
    <div>  
      <h2>Send Message</h2>  
      <form>  
        <div className="mb-3">  
          <label htmlFor="messageContent" className="form-label">Message Content</label>  
          <textarea className="form-control" id="messageContent" rows="4" required></textarea>  
        </div>  
        <button type="submit" className="btn btn-primary">Send Message</button>  
      </form>  
    </div>  
  );  
}  

export default App;
